(function(){
  // Local chart loader: attempt to load Chart.js from CDN so Pages can fetch a local file path (/assets/js/chart.min.js) while still providing Chart.js functionality.
  if (typeof window === 'undefined') return;
  try { if (window.Chart) { console.log('Chart already present'); return; } } catch(e) {}
  var s = document.createElement('script');
  s.src = 'https://cdn.jsdelivr.net/npm/chart.js@4.3.0/dist/chart.umd.min.js';
  s.async = false;
  s.onload = function(){ console.log('Chart.js loaded via local chart.min.js (CDN)'); };
  s.onerror = function(){ console.warn('Failed to load Chart.js via local chart.min.js'); };
  document.head.appendChild(s);
})();